import React from 'react';
import { Navbar, Nav, FormControl, Form, Button, ButtonToolbar } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { logo } from '../images/index.js';
import ModalLogin from '../../src/views/pages/ModalLogin';
import UserContext from '../shared/user.context';


class MainMenu extends React.Component {
    render() {
        let modalClose = () => this.setState({ modalShow: false });
        return (
            <UserContext.Consumer>
            { ({ user, handleUserChange }) => (
                <Navbar bg="dark" variant="dark" expand="sm">
                    <LinkContainer to='/'>
                        <Navbar.Brand>
                            <img src= {logo} width="40" height="40" className="d-inline-block align-top" alt="React Bootstrap logo" />
                        </Navbar.Brand>
                    </LinkContainer>

                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">

                            <LinkContainer exact to="/">
                                <Nav.Link>Home</Nav.Link>
                            </LinkContainer>
                            <LinkContainer to="/order">
                                <Nav.Link>Order</Nav.Link>
                            </LinkContainer>                        
                            <LinkContainer>
                                <ButtonToolbar>
                                    <Button variant="primary" onClick={() => this.setState({ modalShow: true })}> Log In </Button>
                                    {/* <ModalLogin show={this.state.modalShow} onHide={modalClose} /> */}
                                </ButtonToolbar> 
                            </LinkContainer>
                        </Nav>
                        <Form inline>
                            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                            <Button variant="outline-success">Search</Button>
                        </Form>
                    </Navbar.Collapse>
                </Navbar>
            ) }
            </UserContext.Consumer>
        );
    }
}


export default MainMenu;
