import React from 'react';
import UserContext from '../../shared/user.context';
import { logo, appeteasers, fino_side, peri_peri_chicken, sharing_platters, dessert, sides, cataplana_algarve, salads, burgers_pitas_wraps } from '../../images/index.js';
import '../../App.css';
import '../../Order.css';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Container, Row, Col, Image,Card, CardDeck} from 'react-bootstrap';

class Order extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            category: [],
        };
        this.apiUrl = "http://localhost:3004/menu";

    }

    async componentDidMount() {
        const resp = await axios.get(this.apiUrl);

        this.setState({category: resp.data});
       console.log(resp.data);
    }

    render() {

        return (
            <>
                <UserContext.Consumer>
                    {({ user }) => (
                        <>
                        <Container>
                            <header className="App-header">
                                <img src={logo} className="App-logo" alt="menu" />
                            </header>
                            <p>Welcome, {user.name}</p>
                        </Container>
                        <Container fluid className="categ_container">
                            <Row>
                                 {this.state.category.map(categ =>
                                    // <Col sm={4} className="categ">
                                    //     <Link to={"/ordermenu/" + categ.id}>                                                                                         
                                    //         <Image src={"images/" + categ.images} alt="" name={categ.name} title={categ.name} fluid/> 
                                    //     </Link>
                                    //     <Link to={"/ordermenu/" + categ.id}>
                                    //         <span className="name_categ">{categ.name}</span>                                       
                                    //     </Link>                                       
                                    // </Col>
                                    <CardDeck>
                                        <Link to={"/ordermenu/" + categ.id}>
                                        <Card>
                                        <Link to={"/ordermenu/" + categ.id}>
                                            <Card.Img variant="top" src={"images/" + categ.images}  />
                                        </Link>
                                        <Card.Body>
                                            <Card.Title>{categ.name}</Card.Title>
                                            <Card.Text>
                                            This is a wider card with supporting text below as a natural lead-in to
                                            additional content. This content is a little bit longer.
                                            </Card.Text>
                                        </Card.Body>
                                        <Card.Footer>
                                            <Link to={"/ordermenu/" + categ.id}>
                                                <small className="text-muted">Order Now!</small>                                       
                                            </Link>   
                                         </Card.Footer>
                                        </Card>  
                                        </Link>                                  
                                  </CardDeck>
                                )}
                            </Row>
                        </Container>
                    </>
                    )}
                </UserContext.Consumer>
           </>
        );
    }}
export default Order;
