import React  from 'react';
import { Carousel, Col, Image } from 'react-bootstrap'
import { tst_1, tst_2, tst_3, quat, quat_1, } from '../../images/index.js';

class TestimonialCarousel extends React.Component {
    constructor(props, context) {
      super(props, context);
  
      this.handleSelect = this.handleSelect.bind(this);
  
      this.state = {
        index: 0,
        direction: null,
      };
    }
  
    handleSelect(selectedIndex, e) {
      this.setState({
        index: selectedIndex,
        direction: e.direction,
      });
    }
  
    render() {
      const { index, direction } = this.state;
  
      return (
        <Carousel
          activeIndex={index}
          direction={direction}
          onSelect={this.handleSelect}
        >
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=First slide&bg=373940"
              alt="First slide"
            />
            <Carousel.Caption>
              <h3>First slide label</h3>
              <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Col xs={12} md={4} className="">
                                    <Image className="homepage_middle_img" src={tst_1} alt="Trophy" />
                                        <Col className="">                                           
                                            <p className=""><Image className="quat" src={quat} alt=""/>
                                                By far the best online service <br></br>out there... I recommend them to <br></br>every business owner I know.
                                                <Image className="quat_sec" src={quat_1} alt=""/> 
                                            </p>
                                            <span className="testimonial_name">Frank, Napoliâ€™s Pizzeria</span>
                                            
                                        </Col>
                                    </Col>




          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=Second slide&bg=282c34"
              alt="Third slide"
            />
  
            <Carousel.Caption>
              <h3>Second slide label</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=Third slide&bg=20232a"
              alt="Third slide"
            />
  
            <Carousel.Caption>
              <h3>Third slide label</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      );
    }
  }
  
  export default TestimonialCarousel;
