import React, { Component } from 'react';
import '../../App.css';
import '../../Login.css';
import FacebookLogin from 'react-facebook-login';
import GoogleLogin from 'react-google-login';
import UserContext from '../../shared/user.context';
import { Redirect } from 'react-router-dom';
import { Button, FormGroup, FormControl, FomrLabel } from "react-bootstrap";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: 1,
      username: '',
      password: '',
      email: '',
      redirect: false
    }
  }

  validateForm() {
    return this.state.email.length > 0 && this.state.password.length > 0;
  }


  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

handleSubmit = event => {
    event.preventDefault();
  }




  responseFacebook = (response) => {
    //console.log(response);
    console.log(this.context);
    this.context.handleUserChange(response);
    this.setState({
      redirect: true
    });
  }

  responseGoogle = (response) => {
    console.log(response);

    this.context.handleUserChange(response.profileObj);
    this.setState({
      redirect: true
    });
  }

  render() {
      if (this.state.redirect === true) {
        return <Redirect to='/' />
      }
      return (
        <UserContext.Consumer>
          { ({ user, handleUserChange }) => (
            <div className="App">
                
              <p>Welcome, {user.name}</p>
              <div className="Login">
                <form onSubmit={this.handleSubmit}>
                  <FormGroup controlId="email" bsSize="large">
                    <FomrLabel>Email</FomrLabel>
                    <FormControl
                      autoFocus
                      type="email"
                      value={this.state.email}
                      onChange={this.handleChange}
                    />
                  </FormGroup>
                  <FormGroup controlId="password" bsSize="large">
                    <FomrLabel>Password</FomrLabel>
                    <FormControl
                      value={this.state.password}
                      onChange={this.handleChange}
                      type="password"
                    />
                  </FormGroup>
                  <Button
                    block
                    bsSize="large"
                    disabled={!this.validateForm()}
                    type="submit"
                  >
                    Login
                 </Button>
                </form>
              </div>





              <FacebookLogin
                appId="1992350001069404" //APP ID NOT CREATED YET
                fields="name,email,picture"
                callback={this.responseFacebook}
              />
              <br />
              <br />
              <GoogleLogin
                clientId="903697692149-j4q2rsmltp40gik0lb4ciqk3m9d59e4k.apps.googleusercontent.com" //CLIENTID NOT CREATED YET
                buttonText="LOGIN WITH GOOGLE"
                onSuccess={this.responseGoogle}
                onFailure={this.responseGoogle}
              />

            </div>
          ) }
          </UserContext.Consumer>
    );
  }
}

Login.contextType = UserContext;

export default Login;
