import React, { Component } from 'react';
import '../../App.css';
import '../../Login.css';
import FacebookLogin from 'react-facebook-login';
import GoogleLogin from 'react-google-login';
import UserContext from '../../shared/user.context';
import { Redirect } from 'react-router-dom';
import axios from 'axios';
import { Container, Row, Col, Image} from 'react-bootstrap';
import { Link } from 'react-router-dom';

class Login extends Component {
  constructor(props) {
      super(props); 
      this.apiUrl = "http://localhost:3004/menu";
      this.state = {
        category: [],
        id: 1,
        name: '',
        email: '',
        password: '',
        redirect: false
      };    
  }

  async componentDidMount() {
    const resp = await axios.post(this.apiUrl, {admin:
     
        { 
          id:1,
          name: 'Daniel',
          email: "ancutadaniel@gmail.com",
          password: 'Start1234?'
        }     
    
    });

    const newResp = await axios.get(this.apiUrl); 
      this.setState({category: newResp.data});
      console.log(newResp.data);
  }



  responseFacebook = (response) => {
    //console.log(response);
    console.log(this.context);
    this.context.handleUserChange(response);
    this.setState({
      redirect: true
    });
  }

  responseGoogle = (response) => {
    console.log(response);

    this.context.handleUserChange(response.profileObj);
    this.setState({
      redirect: true
    });
  }

  

  render() {
      if (this.state.redirect === true) {
        return <Redirect to='/' />
      }
      return (
        <UserContext.Consumer>
          { ({ user, handleUserChange }) => (
            <div className="App">
                <h2>Welcome, {user.name}</h2>
                <div>
                {this.state.category.map(categ =>
                                    <Col sm={4} className="categ">
                                        <Link to={"/ordermenu/" + categ.id}>
                                                <span className="name_categ">{categ.name}</span>                                           
                                            <Image src={"images/" + categ.images} alt="" name={categ.name} title={categ.name} fluid/> 
                                        </Link>
                                    </Col>
                                )}
                  
                </div>
                <FacebookLogin
                    appId="1992350001069404" //APP ID NOT CREATED YET
                    fields="name,email,picture"
                    callback={this.responseFacebook}
                />
                <br />
                <br />
                <GoogleLogin
                    clientId="903697692149-j4q2rsmltp40gik0lb4ciqk3m9d59e4k.apps.googleusercontent.com" //CLIENTID NOT CREATED YET
                    buttonText="LOGIN WITH GOOGLE"
                    onSuccess={this.responseGoogle}
                    onFailure={this.responseGoogle}
                />

            </div>
          ) }
          </UserContext.Consumer>
    );
  }
}

Login.contextType = UserContext;

export default Login;
